/* Reset và font chữ */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Roboto', sans-serif;
}

body {
    background-color: #f5f7fa;
    color: #333;
    line-height: 1.6;
}

.container {
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
}

/* Header */
.header {
    text-align: center;
    margin-bottom: 30px;
    padding: 20px 0;
}

.header h1 {
    font-size: 2.2rem;
    color: #2c3e50;
    font-weight: 700;
}

.header .icon {
    font-size: 1.8rem;
    vertical-align: middle;
    margin-right: 10px;
}

/* Phần tạo khóa */
.key-section {
    background: white;
    border-radius: 10px;
    padding: 20px;
    margin-bottom: 25px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.key-options {
    margin-bottom: 15px;
}

.key-options label {
    display: block;
    font-weight: 500;
    margin-bottom: 8px;
    color: #555;
}

.radio-group {
    display: flex;
    gap: 15px;
    margin-bottom: 15px;
}

.radio-group label {
    display: flex;
    align-items: center;
    gap: 5px;
    font-weight: normal;
    cursor: pointer;
}

.custom-key-form {
    margin: 15px 0;
}

.custom-key-form label {
    display: block;
    font-weight: 500;
    margin-bottom: 8px;
    color: #555;
}

.custom-key-input {
    display: flex;
    gap: 10px;
}

.custom-key-input input {
    flex-grow: 1;
    padding: 10px 15px;
    border: 1px solid #ddd;
    border-radius: 6px;
    font-size: 1rem;
}

.set-key-btn {
    background-color: #9b59b6;
    color: white;
    border: none;
    padding: 10px 15px;
    border-radius: 6px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s;
}

.set-key-btn:hover {
    background-color: #8e44ad;
    transform: translateY(-2px);
}

.generate-btn {
    background-color: #27ae60;
    color: white;
    border: none;
    padding: 12px 20px;
    border-radius: 6px;
    cursor: pointer;
    font-weight: 500;
    font-size: 1rem;
    transition: all 0.3s;
    display: flex;
    align-items: center;
    width: 100%;
    justify-content: center;
    margin-bottom: 15px;
}

.generate-btn:hover {
    background-color: #219653;
    transform: translateY(-2px);
}

.key-display {
    margin-top: 15px;
}

.key-display label {
    display: block;
    font-weight: 500;
    margin-bottom: 5px;
    color: #555;
}

.key-value {
    background: #f8f9fa;
    padding: 10px 15px;
    border-radius: 6px;
    border: 1px solid #ddd;
    word-break: break-all;
    font-family: monospace;
    margin-bottom: 5px;
}

.key-info {
    font-size: 0.9rem;
    color: #666;
}

/* ... [keep all other existing styles unchanged] ... */

/* Responsive */
@media (max-width: 768px) {
    .radio-group {
        flex-direction: column;
        gap: 8px;
    }
    
    .custom-key-input {
        flex-direction: column;
    }
    
    .header h1 {
        font-size: 1.8rem;
    }
    
    .action-card h2 {
        font-size: 1.2rem;
    }
}
